// Robustes HTML Content Cleanup V2 f√ºr Business Central Blogs
// Optimiert f√ºr: waldo.be, demiliani.com, vjeko.com, yzhums.com, hougaard.com, kauffmann.nl, mynavblog.com, katson.com
// V2: Bessere Extraktion f√ºr verschachtelte divs und Elementor-Seiten

function cleanHtml(html) {
  if (!html) return "";

  let content = null;

  // 1. CONTENT EXTRAKTION - Verbesserte Strategien

  // Strategie A: entry-content (WordPress) - OHNE schlie√üendes div (wegen Verschachtelung)
  const entryMatch = html.match(/<div[^>]*class="[^"]*\bentry-content\b[^"]*"[^>]*>([\s\S]+)/i);
  if (entryMatch) {
    let inner = entryMatch[1];
    // Finde nat√ºrliches Ende (footer, comments, etc.)
    const endPatterns = [
      /<footer\b/i,
      /<div[^>]*class="[^"]*\b(?:comments|comment-respond|related|share-buttons?|author-box|post-navigation|yarpp-related)\b/i,
      /<section[^>]*class="[^"]*\b(?:comments|related)\b/i,
      /<!--\s*[.\/]entry-content/i,
      /<\/article\s*>/i,
      /<div[^>]*id="comments"/i
    ];
    let endPos = inner.length;
    for (const pat of endPatterns) {
      const m = inner.match(pat);
      if (m && m.index < endPos && m.index > 500) endPos = m.index;
    }
    if (endPos > 500) content = inner.substring(0, endPos);
  }

  // Strategie B: Elementor - Sammle ALLE widget-container mit Absatz-Content (ZUERST, da vollst√§ndiger)
  if (!content || content.length < 500) {
    const elementorBlocks = [];
    const regex = /<div class="elementor-widget-container">([\s\S]+?)<\/div>\s*<\/div>/gi;
    let match;
    while ((match = regex.exec(html)) !== null) {
      const block = match[1];
      // √úberspringe Comment-Formulare und Author-Boxen
      if (block.includes('Leave a Reply') || block.includes('Cancel reply')) continue;
      // Nur Bl√∂cke mit echtem Textinhalt (<p> Tags und >15 W√∂rter)
      if (block.includes('<p') || block.includes('<h')) {
        const textOnly = block.replace(/<[^>]+>/g, ' ');
        const words = textOnly.split(/\s+/).filter(w => w.length > 2).length;
        if (words > 15) elementorBlocks.push(block);
      }
    }
    if (elementorBlocks.length >= 3) {
      // Wenn 3+ Bl√∂cke gefunden, ist dies wahrscheinlich Elementor-Content
      content = elementorBlocks.join('\n\n');
    }
  }

  // Strategie B2: Elementor theme-post-content (Fallback f√ºr Video-Posts wie hougaard.com)
  if (!content || content.length < 100) {
    const themePostMatch = html.match(/data-widget_type="theme-post-content[^"]*"[^>]*>\s*<div class="elementor-widget-container">([\s\S]+?)<\/div>\s*<\/div>/i);
    if (themePostMatch && themePostMatch[1].length > 50) {
      content = themePostMatch[1];
    }
  }

  // Strategie C: article Tag
  if (!content || content.length < 500) {
    const articleMatch = html.match(/<article[^>]*>([\s\S]+?)<\/article>/i);
    if (articleMatch && articleMatch[1].length > (content ? content.length : 0)) {
      content = articleMatch[1];
    }
  }

  // Strategie D: post-content div (√§hnlich wie entry-content)
  if (!content || content.length < 500) {
    const postMatch = html.match(/<div[^>]*class="[^"]*\bpost-content\b[^"]*"[^>]*>([\s\S]+)/i);
    if (postMatch) {
      let inner = postMatch[1];
      const endPatterns = [/<footer\b/i, /<\/article\s*>/i, /<div[^>]*class="[^"]*\bcomments\b/i];
      let endPos = Math.min(inner.length, 50000);
      for (const pat of endPatterns) {
        const m = inner.match(pat);
        if (m && m.index < endPos && m.index > 500) endPos = m.index;
      }
      if (endPos > (content ? content.length : 0)) content = inner.substring(0, endPos);
    }
  }

  // Strategie E: main Tag
  if (!content || content.length < 500) {
    const mainMatch = html.match(/<main[^>]*>([\s\S]+?)<\/main>/i);
    if (mainMatch && mainMatch[1].length > (content ? content.length : 0)) {
      content = mainMatch[1];
    }
  }

  // Fallback: Body
  if (!content || content.length < 300) {
    const bodyMatch = html.match(/<body[^>]*>([\s\S]+?)<\/body>/i);
    content = bodyMatch ? bodyMatch[1] : html;
  }

  // 2. NOISE ENTFERNUNG - Aggressive Bereinigung
  const noisePatterns = [
    // Scripts und Styles
    /<script[^>]*>[\s\S]*?<\/script>/gi,
    /<style[^>]*>[\s\S]*?<\/style>/gi,
    /<noscript[^>]*>[\s\S]*?<\/noscript>/gi,
    // Strukturelle Elemente
    /<nav[^>]*>[\s\S]*?<\/nav>/gi,
    /<footer[^>]*>[\s\S]*?<\/footer>/gi,
    /<header[^>]*>[\s\S]*?<\/header>/gi,
    /<aside[^>]*>[\s\S]*?<\/aside>/gi,
    // Embeds und Forms
    /<iframe[^>]*>[\s\S]*?<\/iframe>/gi,
    /<form[^>]*>[\s\S]*?<\/form>/gi,
    /<svg[^>]*>[\s\S]*?<\/svg>/gi,
    // Kommentare
    /<!--[\s\S]*?-->/gi,
    // WordPress-spezifische Noise
    /<div[^>]*class="[^"]*\b(?:sharedaddy|share-buttons|social-share|post-ratings|author-box|related-posts|comments-area|newsletter|cookie|popup|modal|sidebar|widget|advertisement|wp-block-embed|jetpack)\b[^"]*"[^>]*>[\s\S]*?<\/div>/gi,
    // Interaktive Elemente
    /<(?:button|input|select|textarea)[^>]*\/?>(?:[\s\S]*?<\/(?:button|select|textarea)>)?/gi,
    // Bilder mit langen Data-URLs
    /<img[^>]*src="data:[^"]{100,}"[^>]*>/gi,
    // Rating/Vote Widgets
    /<div[^>]*class="[^"]*\bpost-ratings\b[^"]*"[^>]*>[\s\S]*?<\/div>/gi
  ];

  for (const pattern of noisePatterns) {
    content = content.replace(pattern, ' ');
  }

  // 3. CODE-BL√ñCKE BEWAHREN
  const codeBlocks = [];
  content = content.replace(/<pre[^>]*>([\s\S]*?)<\/pre>/gi, (match, code) => {
    const cleanCode = code.replace(/<[^>]+>/g, '').trim();
    if (cleanCode.length > 10) {
      codeBlocks.push(cleanCode);
      return `\n\n[CODE_BLOCK_${codeBlocks.length - 1}]\n\n`;
    }
    return '';
  });

  // 4. STRUKTUR IN TEXT UMWANDELN
  // √úberschriften
  content = content.replace(/<h1[^>]*>([\s\S]*?)<\/h1>/gi, '\n\n# $1\n');
  content = content.replace(/<h2[^>]*>([\s\S]*?)<\/h2>/gi, '\n\n## $1\n');
  content = content.replace(/<h3[^>]*>([\s\S]*?)<\/h3>/gi, '\n\n### $1\n');
  content = content.replace(/<h[4-6][^>]*>([\s\S]*?)<\/h[4-6]>/gi, '\n\n#### $1\n');
  // Paragraphen und Breaks
  content = content.replace(/<\/p>/gi, '\n\n');
  content = content.replace(/<br\s*\/?>/gi, '\n');
  // Listen
  content = content.replace(/<li[^>]*>/gi, '\n‚Ä¢ ');
  content = content.replace(/<\/li>/gi, '');
  // Hervorhebungen (optional behalten)
  content = content.replace(/<strong[^>]*>([\s\S]*?)<\/strong>/gi, '**$1**');
  content = content.replace(/<b>([\s\S]*?)<\/b>/gi, '**$1**');
  content = content.replace(/<em[^>]*>([\s\S]*?)<\/em>/gi, '_$1_');
  content = content.replace(/<code[^>]*>([\s\S]*?)<\/code>/gi, '`$1`');
  // Links - nur Text behalten
  content = content.replace(/<a[^>]*>([\s\S]*?)<\/a>/gi, '$1');

  // 5. ALLE √úBRIGEN TAGS ENTFERNEN
  content = content.replace(/<[^>]+>/g, ' ');

  // 6. HTML-ENTITIES DECODIEREN
  const entities = {
    '&amp;': '&', '&lt;': '<', '&gt;': '>', '&quot;': '"',
    '&#39;': "'", '&apos;': "'", '&nbsp;': ' ',
    '&mdash;': '‚Äî', '&ndash;': '‚Äì', '&hellip;': '‚Ä¶',
    '&copy;': '¬©', '&reg;': '¬Æ', '&trade;': '‚Ñ¢',
    '&euro;': '‚Ç¨', '&pound;': '¬£', '&yen;': '¬•',
    '&laquo;': '¬´', '&raquo;': '¬ª', '&bull;': '‚Ä¢'
  };
  for (const [entity, char] of Object.entries(entities)) {
    content = content.replace(new RegExp(entity, 'gi'), char);
  }
  // Numerische Entities
  content = content.replace(/&#(\d+);/g, (m, n) => {
    try { return String.fromCharCode(parseInt(n)); } catch(e) { return ''; }
  });
  content = content.replace(/&#x([0-9a-f]+);/gi, (m, n) => {
    try { return String.fromCharCode(parseInt(n, 16)); } catch(e) { return ''; }
  });

  // 7. CODE-BL√ñCKE WIEDERHERSTELLEN
  for (let i = 0; i < codeBlocks.length; i++) {
    content = content.replace(`[CODE_BLOCK_${i}]`, `\n\`\`\`\n${codeBlocks[i]}\n\`\`\`\n`);
  }

  // 8. WHITESPACE NORMALISIEREN
  content = content.replace(/[ \t]+/g, ' ');
  content = content.replace(/\n[ \t]+/g, '\n');
  content = content.replace(/[ \t]+\n/g, '\n');
  content = content.replace(/\n{3,}/g, '\n\n');
  content = content.trim();

  // 9. INTELLIGENTES ABSCHNEIDEN bei 8000 Zeichen
  const maxLen = 8000;
  if (content.length > maxLen) {
    content = content.substring(0, maxLen);
    // Schneide am Satzende oder Absatz ab
    const lastPeriod = content.lastIndexOf('. ');
    const lastNewline = content.lastIndexOf('\n\n');
    const cutPos = Math.max(lastPeriod, lastNewline);
    if (cutPos > maxLen * 0.75) {
      content = content.substring(0, cutPos + 1);
    }
  }

  return content;
}

const input = $input.first();
const htmlContent = input.json.data || input.json.body || '';
const cleanContent = cleanHtml(htmlContent);

return [{
  json: {
    ...input.json,
    data: cleanContent
  }
}];

// 1. Den Text aus dem Input holen
let rawText = $input.first().json.text;

// 2. Die Markdown-Formatierung (```json und ```) entfernen
const cleanJsonString = rawText.replace(/```json\n?|```/g, '').trim();

// 3. pubDate aus dem originalen RSS-Item holen (via Merge1)
const pubDate = $('Merge1').item.json.pubDate || $('Merge1').item.json.isoDate || '';

try {
  const parsedData = JSON.parse(cleanJsonString);

  // pubDate an das Ergebnis anh√§ngen
  return {
    json: {
      ...parsedData,
      pubDate: pubDate
    }
  };
} catch (error) {
  return {
    json: {
      error: "Parsing fehlgeschlagen",
      raw: cleanJsonString
    }
  };
}

// Parse Kategorisierung und f√ºge sie zum Summary-Ergebnis hinzu
let rawText = $input.first().json.text;
const cleanJsonString = rawText.replace(/```json\n?|```/g, '').trim();

// Summary-Daten aus dem vorherigen Convert to Json Node holen
const summaryData = $('Convert to Json').item.json;

try {
  const catData = JSON.parse(cleanJsonString);
  return {
    json: {
      ...summaryData,
      Category: catData.Category || 'News',
      Tags: catData.Tags || [catData.Category || 'News']
    }
  };
} catch (error) {
  // Fallback: Kategorie 'News' verwenden
  return {
    json: {
      ...summaryData,
      Category: 'News',
      Tags: ['News'],
      categoryError: 'Kategorisierung fehlgeschlagen: ' + error.message
    }
  };
}

// Pr√ºfe ob YouTube-Video vorhanden und wenig Content
// Falls ja, extrahiere Video-ID f√ºr Transkript-Abruf

const input = $input.first();
const content = input.json.data || '';
const htmlContent = $('Fetch Page Content').item.json.data || '';

// Suche nach YouTube-URLs in Content oder Original-HTML
const patterns = [
  /(?:youtube\.com\/watch\?v=|youtu\.be\/|youtube\.com\/embed\/)([a-zA-Z0-9_-]{11})/,
  /youtube\.com\/watch\?[^"']*v=([a-zA-Z0-9_-]{11})/
];

let videoId = null;
for (const pattern of patterns) {
  const match = (content + htmlContent).match(pattern);
  if (match) {
    videoId = match[1];
    break;
  }
}

// Nur Transkript holen wenn: wenig Content (<500 Zeichen) UND YouTube-Video gefunden
const needsTranscript = content.length < 500 && videoId !== null;

return [{
  json: {
    ...input.json,
    youtubeVideoId: videoId,
    needsTranscript: needsTranscript
  }
}];

// Verarbeite Supadata Transcript-Response
const input = $input.first();
const originalData = $('Check YouTube Video').item.json;

let transcript = '';

try {
  // Supadata gibt content als Array von {text, offset, duration} zur√ºck
  const content = input.json.content || [];

  if (Array.isArray(content)) {
    transcript = content
      .map(segment => segment.text || '')
      .filter(t => t && t.trim())
      .join(' ')
      .replace(/\s+/g, ' ')
      .trim();
  } else if (typeof content === 'string') {
    transcript = content;
  }
} catch (e) {
  transcript = '';
}

// Kombiniere Original-Content mit Transkript
let finalContent = originalData.data || '';

if (transcript && transcript.length > 100) {
  finalContent = finalContent + '\n\n---\n\n## üìπ Video Transcript\n\n' + transcript;
}

return [{
  json: {
    ...originalData,
    data: finalContent,
    hasTranscript: transcript.length > 100,
    transcriptLength: transcript.length
  }
}];

// Kein Transcript verf√ºgbar - Original-Daten zur√ºckgeben
const input = $input.first();

return [{
  json: {
    ...input.json,
    hasTranscript: false
  }
}];

// Bereits gespeicherte URLs aus der Datenbank laden (via API)
let existingUrls = [];
try {
  const apiResponse = $('Get Already Sent').first();
  existingUrls = apiResponse.json.urls || [];
} catch(e) {
  // API nicht erreichbar - sicherheitshalber nichts filtern
  // (Duplikate werden von INSERT IGNORE in write.php abgefangen)
  existingUrls = [];
}
const sentLinks = new Set(existingUrls);

// Nur Items behalten deren Link noch nicht in der DB ist
const results = [];
for (const item of $input.all()) {
  const link = item.json.link || '';
  if (link && !sentLinks.has(link)) {
    results.push(item);
  }
}

// Wenn nichts Neues da ist, leeres Array zur√ºckgeben
return results.length > 0 ? results : [];

// Transform Loop-Output zu Posts-Array f√ºr die API
// KW und Jahr werden von PHP anhand des Datums berechnet
const articles = $input.all();

// Deduplicate by Link
const seen = new Set();
const posts = [];
for (const item of articles) {
  const a = item.json;
  if (a.error) continue; // KI-Parsing fehlgeschlagen
  const link = a.Link || a.link;
  if (!link || seen.has(link)) continue;
  seen.add(link);

  // pubDate aus RSS -> ISO-Datum umwandeln
  let date = '';
  if (a.pubDate) {
    try {
      date = new Date(a.pubDate).toISOString().split('T')[0];
    } catch(e) {
      date = new Date().toISOString().split('T')[0];
    }
  } else {
    date = new Date().toISOString().split('T')[0];
  }

  posts.push({
    title: a.Title || a.title,
    summary: a.Summary || a.summary,
    source: a.Creator || a.creator || 'Unbekannt',
    sourceUrl: link,
    category: a.Category || a.category || 'News',
    date: date,
    tags: (a.Tags || a.tags || [a.Category || a.category]).filter(Boolean)
  });
}

return [{
  json: {
    posts: posts
  }
}];

